package com.texes.app;

public class starp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int i;
     int j;
     int k=5;
     for (i=1;i<=0;i++) {
     for (j=(k-i);j>=0;j++) {
    	 System.out.print(" ");
     }
     for(j=1;j<=i;j++) {
    	 System.out.print("* ");
    	 }
     System.out.println();
     }
	}

}
